import React, { useEffect, useState } from "react";
import useFetch from "react-fetch-hook";
import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

interface Department {
  id: number;
  name: string;
}
const schema = yup.object().shape({
  name: yup.string().required(),
  date: yup.date().required(),
  email: yup
    .string()
    .email()
    .required(),
  check: yup.bool().required(),
});

const getUrl =
  "https://ddh-front-default-rtdb.europe-west1.firebasedatabase.app/departments.json";
const postUrl =
  "https://ddh-front-default-rtdb.europe-west1.firebasedatabase.app/users.json";

export const Form = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm({ resolver: yupResolver(schema) });
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("");

  const onSubmit = () => {
    fetch(postUrl, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => {
        if (response.status !== 200) {
          throw new Error(response.statusText);
        }
        return response.json();
      })
      .then(() => {
        setMessage("Dane zostały poprawnie zapisane");
        setStatus("success");
      })
      .catch((err) => {
        setMessage(err.toString());
        setStatus("error");
      });
  };

  const { isLoading, data, error } = useFetch<Department[]>(getUrl);

  return isLoading ? (
    <h1>Ładowanie...</h1>
  ) : (
    <form className="mt-5" onSubmit={handleSubmit(onSubmit)}>
      {message && <h2>{message}</h2>}
      <div className="mb-3">
        <label htmlFor="user-name" className="form-label">
          Imię i nazwisko
        </label>
        <input
          {...register("name")}
          type="name"
          className="form-control"
          id="user-name"
          placeholder="Imię i nazwisko"
          name="fullName"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="user-birth-date" className="form-label">
          Data urodzenia
        </label>
        <input
          {...register("date")}
          type="text"
          className="form-control"
          id="user-birth-date"
          placeholder="DD/MM/YYYY"
          name="birthDate"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="user-email" className="form-label">
          Email
        </label>
        <input
          {...register("email")}
          type="email"
          className="form-control"
          id="user-email"
          placeholder="user@example.com"
          name="email"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="user-department" className="form-label">
          Wydział
        </label>
        <select className="form-select" name="department" id="user-department">
          <option disabled hidden>
            Wybierz oddział
          </option>
          {data &&
            data.map(({ id, name }) => (
              <option key={id} value={id}>
                {name}
              </option>
            ))}
        </select>
      </div>
      {error && (
        <div>{`Wystąpił problem podczas pobierania danych: ${error}`}</div>
      )}
      <div className="form-check">
        <input
          className="form-check-input"
          type="checkbox"
          value=""
          id="form-terms"
          name="termsOfUse"
        />
        <label
          className="form-check-label"
          htmlFor="form-terms"
          {...register("check")}
        >
          Akceptuję regulamin
        </label>
      </div>
      <div className="d-grid gap-2 d-md-flex justify-content-md-end">
        <button type="submit" className="btn btn-primary">
          Zapisz
        </button>
      </div>
    </form>
  );
};
