import * as yup from "yup";
var schema = yup.object().shape({
  name: yup.string().required("Dish name is required"),
  preparationTime: yup.date().required("Time is required").typeError("Pleasw use HH:MM:SS format"),
  email: yup.string().email("Email musi być poprawnym adresem").required("Email jest wymagany"),
  department: yup.string().test("val", "Proszę wybierz oddział", function (val) {
    return val != "Wybierz oddział";
  }).required(),
  checkbox: yup.bool().oneOf([true], "Aby kontynuować zaakceptuj regulamin")
});
export default schema;
