import React, { useEffect, useState } from "react";
import useFetch from "react-fetch-hook";

interface Department {
  id: number;
  name: string;
}

const url: string = `https://ddh-front-default-rtdb.europe-west1.firebasedatabase.app/departments.json`;

export const Form = () => {
  const [status, setStatus] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = () => {
    fetch(
      "https://ddh-front-default-rtdb.europe-west1.firebasedatabase.app/users.json",
      {
        method: "POST",
        mode: "cors",
        body: JSON.stringify(data),
      }
    )
      .then((response) => {
        if (response.status !== 200) {
          throw new Error(response.statusText);
        }

        return response.json();
      })

      .then(() => {
        setMessage("We'll be in touch soon.");

        setStatus("success");
      })

      .catch((err) => {
        setMessage(err.toString());

        setStatus("error");
      });
  };

  const { isLoading, data, error } = useFetch<Department[]>(url);

  return isLoading ? (
    <h1>Ładowanie...</h1>
  ) : (
    <form className="mt-5" onSubmit={handleSubmit}>
      <div className="mb-3">
        <label htmlFor="user-name" className="form-label">
          Imię i nazwisko
        </label>
        <input
          type="name"
          className="form-control"
          id="user-name"
          placeholder="Imię i nazwisko"
          name="fullName"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="user-birth-date" className="form-label">
          Data urodzenia
        </label>
        <input
          type="text"
          className="form-control"
          id="user-birth-date"
          placeholder="DD/MM/YYYY"
          name="birthDate"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="user-email" className="form-label">
          Email
        </label>
        <input
          type="email"
          className="form-control"
          id="user-email"
          placeholder="user@example.com"
          name="email"
        />
      </div>
      <div className="mb-3">
        <label htmlFor="user-department" className="form-label">
          Wydział
        </label>
        <select className="form-select" name="department" id="user-department">
          <option disabled selected hidden>
            Wybierz oddział
          </option>
          {data &&
            data.map(({ id, name }) => (
              <option key={id} value={id}>
                {name}
              </option>
            ))}
        </select>
      </div>
      {error && (
        <div>{`Wystąpił problem podczas pobierania danych: ${error}`}</div>
      )}
      <div className="form-check">
        <input
          className="form-check-input"
          type="checkbox"
          value=""
          id="form-terms"
          name="termsOfUse"
        />
        <label className="form-check-label" htmlFor="form-terms">
          Akceptuję regulamin
        </label>
      </div>
      <div className="d-grid gap-2 d-md-flex justify-content-md-end">
        <button type="button" className="btn btn-primary">
          Zapisz
        </button>
      </div>
    </form>
  );
};
