import * as yup from "yup";
var schema = yup.object().shape({
  dishName: yup.string().required("Dish name is required"),
  preparationTime: yup.date().required("Time is required").typeError("Please use HH:MM:SS format"),
  email: yup.string().email("Email musi być poprawnym adresem").required("Email jest wymagany"),
  dishType: yup.string().required().oneOf(["pizza", "soup", "sandwich"]).label("Selected dish type"),
  checkbox: yup.bool().oneOf([true], "Aby kontynuować zaakceptuj regulamin")
});
export default schema;
