import * as yup from "yup";
var schema = yup.object().shape({
  dishName: yup.string().required("Dish name is required"),
  preparationTime: yup.string().required("Preparation time is required").typeError("Please use HH:MM:SS format"),
  dishType: yup.string().required().oneOf(["pizza", "soup", "sandwich"]).label("Selected dish type"),
  slicesOfBread: yup.number().required().typeError("Please enter a number of slices"),
  spicinessScale: yup.number().typeError("Please enter a 1-10 number").required().min(0, "Min is 0").max(10, "Max is 10")
});
export default schema;
